fleet_package_policy.data_stream_variables_json:
${jsonencode({
  interval = var.interval
  preserve_original_event = var.preserve_original_event
  processors = var.processors_yaml
  tags = var.tags
})}

fleet_package_policy.input_variables_json:
${jsonencode({
  api_host = var.api_host
  delegated_account = var.delegated_account
  enable_request_tracer = var.enable_request_tracer
  http_client_timeout = var.http_client_timeout
  initial_interval = var.initial_interval
  jwt_file = var.jwt_file
  jwt_json = var.jwt_json
  user_key = var.user_key
})}
